var $ = jQuery;

var trim  = function(s) { return typeof(s)=='string'?s.replace(/^\s+|\s+$/g,""):s; };
var ltrim = function(s) { return typeof(s)=='string'?s.replace(/^\s+/,""):s;       };
var rtrim = function(s) { return typeof(s)=='string'?s.replace(/\s+$/,""):s;       };

